def card_list(menu):
    result = ''
    for value in data:
        if value['category'] == menu:
            result = result + f"""
    <div class="col">
     <div class="card" style="width: 18rem;">
          <img src="{value['url']}" class="card-img-top" alt="...">
          <div class="card-body">
            <h5 class="card-title">{value['name']}</h5>
            <p class="card-text">{value['content']}.</p>
          </div>     
         </div>
     </div>
     """
    return result


